void run(vec2 uv,vec3 color, vec2 inValue, vec2 outValue,out vec3 outColor) {
    float denom = inValue.y - inValue.x;
    vec3 t = vec3(
        denom == 0.0 ? 0.0 : (color.x - inValue.x) / denom,
        denom == 0.0 ? 0.0 : (color.y - inValue.x) / denom,
        denom == 0.0 ? 0.0 : (color.z - inValue.x) / denom
    );
   t = clamp(t, 0.0, 1.0);
    outColor= mix(vec3(outValue.x,outValue.x,outValue.x),vec3(outValue.y,outValue.y,outValue.y), t);
}
void run(vec2 uv,float color, vec2 inValue, vec2 outValue,out float outColor) {
    float denom = inValue.y - inValue.x;
    float t =  denom == 0.0 ? 0.0 : (color - inValue.x) / denom;      
   t = clamp(t, 0.0, 1.0);
    outColor= mix(outValue.x,outValue.y, t);
}
