data.glsl:
// Overloaded functions for dynamic typing
void run(vec2 uv, float a, float b, out float res) { res = a + b; }
void run(vec2 uv, vec2 a, vec2 b, out vec2 res)   { res = a + b; }
void run(vec2 uv, vec3 a, vec3 b, out vec3 res)   { res = a + b; }
void run(vec2 uv, vec4 a, vec4 b, out vec4 res)   { res = a + b; }
