data.glsl:
// Simplex noise helper
vec3 permute(vec3 x) { return mod(((x*34.0)+1.0)*x, 289.0); }
float snoise(vec2 v){
  const vec4 C = vec4(0.211324865405187, 0.366025403784439,
           -0.577350269189626, 0.024390243902439);
  vec2 i  = floor(v + dot(v, C.yy) );
  vec2 x0 = v -   i + dot(i, C.xx);
  vec2 i1;
  i1 = (x0.x > x0.y) ? vec2(1.0, 0.0) : vec2(0.0, 1.0);
  vec4 x12 = x0.xyxy + C.xxzz;
  x12.xy -= i1;
  i = mod(i, 289.0);
  vec3 p = permute( permute( i.y + vec3(0.0, i1.y, 1.0 ))
  + i.x + vec3(0.0, i1.x, 1.0 ));
  vec3 m = max(0.5 - vec3(dot(x0,x0), dot(x12.xy,x12.xy),
    dot(x12.zw,x12.zw)), 0.0);
  m = m*m ;
  m = m*m ;
  vec3 x = 2.0 * fract(p * C.www) - 1.0;
  vec3 h = abs(x) - 0.5;
  vec3 ox = floor(x + 0.5);
  vec3 a0 = x - ox;
  m *= 1.79284291400159 - 0.85373472095314 * ( a0*a0 + h*h );
  vec3 g;
  g.x  = a0.x  * x0.x  + h.x  * x0.y;
  g.yz = a0.yz * x12.xz + h.yz * x12.yw;
  return 130.0 * dot(m, g);
}

void run(vec2 uv, float scale, float time, out vec2 vector) {
    vec2 p = uv * scale;
    float eps = 0.1;

    // Calculate curl from noise field potential
    // Curl(F) = (dF_y/dx - dF_x/dy)
    // Here we use a scalar potential field P and calculate curl as (-dP/dy, dP/dx)

    float n1 = snoise(p + vec2(0.0, eps) + time);
    float n2 = snoise(p - vec2(0.0, eps) + time);
    float a = (n1 - n2) / (2.0 * eps);

    float n3 = snoise(p + vec2(eps, 0.0) + time);
    float n4 = snoise(p - vec2(eps, 0.0) + time);
    float b = (n3 - n4) / (2.0 * eps);

    vector = vec2(a, -b);
    // Map from [-1, 1] range to [0, 1] for visualization if needed, 
    // but usually Curl Noise is used as a force/offset which expects negative values.
    // To make it look "normal" as a color map, we can offset it.
    // vector = vector * 0.5 + 0.5; 
}
