data.glsl:
void run(vec2 uv, vec2 cartesian, vec2 center, out vec2 polar, out float radius, out float angle) {
    vec2 delta = cartesian - center;
    radius = length(delta);
    float raw_angle = atan(delta.y, delta.x);
    angle = raw_angle / 6.2831853 + 0.5;
    polar = vec2(radius, angle);
}
void run(vec2 uv, sampler2D cartesian, vec2 center, out vec4 outColor, out float radius, out float angle) {
    vec2 delta = uv - center;
    radius = length(delta);
    float raw_angle = atan(delta.y, delta.x);
    angle = raw_angle / 6.2831853 + 0.5;
  vec2  polar = vec2(radius, angle);
    outColor=texture(cartesian,polar);
}
