data.glsl:
void run(vec2 uv, sampler2D tex, float intensity, float radius, out vec4 result) {
    vec4 original = texture(tex, uv);
    vec3 glowSum = vec3(0.0);
    float totalWeight = 0.0;

    // Simple Gaussian-ish blur
    for (int i = -4; i <= 4; i++) {
        for (int j = -4; j <= 4; j++) {
            vec2 offset = vec2(float(i), float(j)) * radius * 0.002;
            float weight = 1.0 - length(vec2(float(i), float(j))) / 5.0;
            if (weight > 0.0) {
                glowSum += texture(tex, uv + offset).rgb * weight;
                totalWeight += weight;
            }
        }
    }

    vec3 glow = (glowSum / totalWeight) * intensity;
    result = vec4(original.rgb + glow, original.a);
}
